INSERT INTO public.site (id, country, description, image_name, name, region) VALUES (1, 'La Réunion', 'Un petit secteur avec quelques beaux passages. Idéal pour ceux qui habitent dans le coin ! Il n’y a pas de marche d’approche et vous pouvez y aller en famille. Des secteurs à l’ombre pour grimper tout au long de la journée.', 'default_mountain_image.jpg', 'RAVINE CLAUDE', 'La Réunion');
INSERT INTO public.site (id, country, description, image_name, name, region) VALUES (4, 'La Réunion', 'La  ravine  Bernica  recèle  de  nombreux  passages  dans  tout  les  niveaux,  plutôt  dans  le  5  et  le  6  pour le moment. Attention toutefois les réceptions sont  souvent  chaotiques  et  nécessitent  un  bon  agencement   de   crashpad   ainsi   que   de   bons   pareurs.L’accès  peut  être  assez  difficile  à  trouver  dans  les  premiers  mètres  tant  de  nombreuses  sentes  parcours le plateau bordant la ravine. Il n’est pas  facile d’accès pour des enfants (main courante et désescalade).', 'default_mountain_image.jpg', 'RAVINE BERNICA', 'La Réunion');
