INSERT INTO public.sector (id, description, image_name, name, site_id) VALUES (7, 'Depuis le route des Tamarins prenez la sortie “Plateau Caillou”. Après la sortie restez sur la D6 et prenez la déviation de Plateau Caillou. Au rond point allez tout droit en direction de St Gilles les hauts, puis prenez 500 m plus loin la D8 sur votre gauche direction “Bellemène”. Montez sur 900 m et prenez à gauche la D4E, “Bellemène”. Dès que vous aurez passé la ravine Bernica (la première traversée), prenez la route qui descend sur votre gauche (direction “apiculteur”) et garez vous 500 m plus bas, juste avant une nouvelle intersection menant à un apiculteur. Trouvez le marquage 50 m en amont (rubalis et / ou peinture) et suivez le jusqu’au bloc ...', 'default_mountain_image.jpg', 'La Cachette', 4);
